package com.switch_examples;

public class CharIntSwitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int input = 97;

		switch (input) {
		case 'a':
			System.out.println("Welcome a");
			break;

		case 'z':
			System.out.println("Welcome z");
			break;

		}
	}
}
